#include <bits/stdc++.h>using namespace std; bool isPrime(int n) {    if (n <= 1) return false;    if (n <= 3) return true;     if (n % 2 == 0 || n % 3 == 0) return false;     for (int i = 5; i * i <= n; i += 6) {        if (n % i == 0 || n % (i + 2) == 0)            return false;    }    return true;}  int main() {	int t;	cin>>t;	while(t--){	    int n;	    cin>>n;	    vector<int>a(n);	    int sum=0;	    for(int &i:a){	        cin>>i;	        sum+=i;	    }	    	    if(!isPrime(sum)){	        cout<<n<<endl;	        for(int i=0;i<n;i++){	            cout<<i+1<<" ";	        }	        cout<<endl;	        continue;	    }	    int key;	    for(int i=0;i<n;i++){	        if(!isPrime(sum-a[i])){	            key=i;	            break;	        }	    }	    cout<<n-1<<endl;	    for(int i=0;i<n;i++){	        if(i==key)continue;	        cout<<i+1<<" ";	    }	    	    cout<<endl;	} }