#include <bits/stdc++.h>using namespace std; int main() {	int t;	cin>>t;	while(t--){	    int n;	    cin>>n;	    vector<pair<char,int>>cards(n);	    char color = 'R';	    for(int i=0;i<n;i++){	        int val;	        cin>>val;	        cards[i]={color,val};	        if(color=='R'){	            color='B';	        }else{	            color='R';	        }	    }	    	    sort(cards.begin(),cards.end(),[](pair<char,int>a,pair<char,int>b){	        return a.second>b.second;	    });	    bool possible = true;	    for(int i=0;i<n-1;i++){	        if(cards[i].first==cards[i+1].first){	            possible = false;	            break;	        }	    }	    	    (possible)?cout<<"YES":cout<<"NO";	    cout<<endl;	} }