#include <bits/stdc++.h>using namespace std;   int main() {	int t;	cin>>t;	while(t--){	    int n;	    cin>>n;	    vector<int>mex(n);	    int cnt0=0,cnt1=0;	    for(int &i:mex){	        cin>>i;	        if(i==0){	            cnt0++;	        }else if(i==1){	            cnt1++;	        }	    }	    	    if(cnt0==1  || (cnt0>1 && cnt1>=1)){	        cout<<"YES"<<endl;	    }else{	        cout<<"NO"<<endl;	    }	} }